    selector: 'app-port-complete-template-driven',
    templateUrl: './port-complete.component.html'
})
export class PortCompleteTemplateDrivenComponent implements OnInit {
    user: User

    constructor(
    ) { }

    ngOnInit() {
       this.user= new User()
    }
}

